package com.Day2;
import java.util.*;
public class OddorEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner c=new Scanner(System.in);
		int a;
		a=c.nextInt();
		if(a%2==0){
			System.out.println("even");
		}
		else {
			System.out.println("odd");
		}

	}

}
